<?php/** * @link http://www.yiiframework.com/ * @copyright Copyright (c) 2008 Yii Software LLC * @license http://www.yiiframework.com/license/ */namespace common\helpers;use common\models\Article;use yii;/** * ArrayHelper provides additional array functionality that you can use in your * application. * * @author Qiang Xue <qiang.xue@gmail.com> * @since 2.0 */class ArticleHelper{    public static function GetListRand($id,$num){//随机调取新闻某栏目的新闻        $array=array();        if (isset($id)){            $array=Yii::$app->db->createCommand("SELECT id,title,description FROM yii2_article where status=1 and category_id in (".$id.") ORDER BY RAND() LIMIT ".$num)->queryAll();;        }        return $array;    }    public static function CategoryID_get_article($c1,$c2='',$num='',$orderBy='',$np=''){        $query=Article::find()->where(['category_id'=>$c1]);        if (!empty($np)){            $query->andWhere(['like','np',$np]);        }        if (!empty($c2)){            $query->orWhere(['category_id2'=>$c2]);        }        if (!empty($num)){            $query->limit($num);        }        if (!empty($orderBy)){            $query->orderBy($orderBy );        }        $list=$query->all();        return $list;    }}