<?phpnamespace common\models;use Yii;/** * This is the model class for table "yii2_anchor". * * @property integer $id * @property string $name * @property string $url * @property integer $num * @property integer $sort * @property integer $type * @property integer $create_time */class Anchor extends \yii\db\ActiveRecord{    /**     * @inheritdoc     */    public static function tableName()    {        return 'yii2_anchor';    }    /**     * @inheritdoc     */    public function rules()    {        return [            [['num', 'sort','type','create_time'], 'integer'],            [['name'], 'string', 'max' => 255],            [['url'], 'string', 'max' => 255],        ];    }    /**     * @inheritdoc     */    public function attributeLabels()    {        return [            'id' => 'ID',            'type'=>'type',            'name' => 'Name',            'url' => 'Url',            'num' => 'Num',            'create_time' => 'create_time',            'sort' => 'Sort',        ];    }    public function getNum(){        $num=ArticleAnchor::find()->andWhere(['anchor_id' => $this->id])->count('id');        return $num;    }    public function getClick(){        $all=ArticleAnchor::find()->andWhere(['anchor_id' => $this->id])->sum('click');        return $all;    }}