<?phpnamespace frontend\controllers;use common\helpers\ArrayHelper;use common\models\Article;use common\models\Category;use common\models\Keywords;use yii\web\Controller;use yii;use common\models\SendMail;use yii\helpers\Html;class PageController extends CommonController{    /**     * @var string     */    public $layout = 'main';    public function actionIndex()    {        parent::common();        return $this->render('index',['data'=>$this->data]);    }    public function actionCompanyProfile(){        parent::common();        $this->common_breadcrumbs();        return $this->render('aboutus',['data'=>$this->data]);    }    public function actionQualificationsHonors(){        parent::common();        $this->common_breadcrumbs();        return $this->render('aboutus',['data'=>$this->data]);    }    public function actionHistory(){        parent::common();        $this->common_breadcrumbs();        return $this->render('aboutus',['data'=>$this->data]);    }    public function actionContactus(){        parent::common();        Yii::$app->params['breadcrumbs']=array();        Yii::$app->params['breadcrumbs'][]=Html::tag('a',"Home",['href'=>Yii::$app->homeUrl]);//面包屑        Yii::$app->params['breadcrumbs'][]='>'.Html::tag('a',Yii::$app->params['lanmu']['title'],['href'=>'/'.Yii::$app->params['lanmu']['name']]);        return $this->render('contactus',['data'=>$this->data]);    }    public function actionResearchAndDevelopmentTeam(){        parent::common();        $this->common_breadcrumbs();        return $this->render('rd',['data'=>$this->data]);    }    public function actionTestCenter(){        parent::common();        $this->common_breadcrumbs();        return $this->render('rd',['data'=>$this->data]);    }    public function actionResearchPlatformAndPatent(){        parent::common();        $this->common_breadcrumbs();        return $this->render('rd',['data'=>$this->data]);    }    public function actionProductCertification(){        parent::common();        $this->common_breadcrumbs();        return $this->render('rd',['data'=>$this->data]);    }    public function common_breadcrumbs(){        Yii::$app->params['breadcrumbs']=array();        Yii::$app->params['breadcrumbs'][]=Html::tag('a',"Home",['href'=>Yii::$app->homeUrl]);//面包屑        $action= Yii::$app->controller->action->id;//获取方法名        $this->lanmu=Category::find()->where(['like','name',$action])->one();        $self_lanmu=Category::find()->where(['id'=>$this->lanmu['pid']])->one();        Yii::$app->params['breadcrumbs'][]='>'.Html::tag('a',$self_lanmu['title'],['href'=>$self_lanmu['url']]);        Yii::$app->params['breadcrumbs'][]='>'.Html::tag('a',Yii::$app->params['lanmu']['title'],['href'=>'/'.Yii::$app->params['lanmu']['name']]);    }}