<?phpuse yii\helpers\Html;use yii\helpers\HtmlPurifier;use yii\helpers\Url;$arr=explode(".", strip_tags($model->content) );$description=$arr[0].$arr[1].'. ';?><li><div class="item">        <? if (!empty($model->cover)):?>            <div class="right">                <div class="img"><a href="<?=$model->url ?>"><img src="<?=$model->imageUrl?>"/></a></div>            </div>        <?endif;?>        <div class="left   <?= empty($model->cover)?'w100':''?>">            <div class="time size4"><?= \common\helpers\Html::time_en($model->create_time) ?></div>            <div class="title">                <a href="<?=$model->url ?>" class="size2 color_black"><?=$model->title?></a></div>            <div class="text hidden-xs"><p class="size4"><?= Html::encode(empty($model->description)?$description:$model->description) ?></p></div>        </div>    </div></li>